module Day01Spec where

import Day01
import Test.Hspec

spec :: Spec
spec = do
  partOneSpec
  partTwoSpec

partOneSpec :: Spec
partOneSpec =
  describe "Part one" $ do
    it "solves the examples" $
      map partOne ["+1\n-2\n+3\n+1", "+1\n+1\n+1", "+1\n+1\n-2", "-1\n-2\n-3"] `shouldBe`
      [3, 3, 0, -6]
    it "solves the puzzle" $ partOne puzzleInput `shouldBe` 510

partTwoSpec :: Spec
partTwoSpec =
  describe "Part two" $ do
    it "solves the examples" $
      map
        partTwo
        [ "+1\n-2\n+3\n+1"
        , "+1\n1"
        , "+3\n+3\n+4\n-2\n-4"
        , "-6\n+3\n+8\n+5\n-6"
        , "+7\n+7\n-2\n-7\n-4"
        ] `shouldBe`
      [2, 0, 10, 5, 14]

