module Day02Spec where

import Day02
import Test.Hspec

spec :: Spec
spec = do
  partOneSpec
  partTwoSpec

partOneSpec :: Spec
partOneSpec =
  describe "Part one" $ do
    it "solves the examples" $
      partOne "abcdef\nbababc\nabbcde\nabcccd\naabcdd\nabcdee\nababab" `shouldBe`
      12
    it "solves the puzzle" $ partOne puzzleInput `shouldBe` 6642

